import React, { useState, useEffect, useRef } from 'react';
import QRCode from 'qrcode.react';
import axios from 'axios'; // Assuming you're using axios for API calls

const InviteePage = ({ match }) => {
  const [eventDetails, setEventDetails] = useState(null);
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const qrCodeRef = useRef(null); // Ref to access the QR code component

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        // Extract the event ID from the URL params
        const { id } = match.params;

        // Replace with your API endpoint
        const response = await axios.get(`/api/events/${id}`);
        setEventDetails(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch event details.');
        setLoading(false);
      }
    };

    fetchEventDetails();
  }, [match.params]);

  const handleGenerateQrCode = () => {
    if (eventDetails) {
      // URL to encode in QR code
      const url = `${window.location.origin}/events/${eventDetails.id}/attend`;
      setQrCodeUrl(url);
    }
  };

  const handleDownloadQrCode = () => {
    const canvas = qrCodeRef.current.querySelector('canvas');
    if (canvas) {
      const imgURL = canvas.toDataURL('image/png');
      const link = document.createElement('a');
      link.href = imgURL;
      link.download = 'event-qr-code.png';
      link.click();
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="invitee-page">
      <h2>{eventDetails.name}</h2>
      <p><strong>Date:</strong> {eventDetails.date}</p>
      <p><strong>Location:</strong> {eventDetails.location}</p>
      <p><strong>Description:</strong> {eventDetails.description}</p>

      <button onClick={handleGenerateQrCode} className="btn btn-primary">
        Generate QR Code
      </button>

      {qrCodeUrl && (
        <div className="qr-code-display" ref={qrCodeRef}>
          <QRCode value={qrCodeUrl} />
          <button onClick={handleDownloadQrCode} className="btn btn-secondary">
            Download QR Code
          </button>
        </div>
      )}
    </div>
  );
};

export default InviteePage;
