import React, { useState } from 'react';
import './UserManagement.css'; // Import the CSS file for UserManagement
import Sidebar from './Sidebar';

const initialUsers = [
    { id: 1, name: 'Alice Johnson', email: 'alice.johnson@example.com', role: 'Admin' },
    { id: 2, name: 'Bob Smith', email: 'bob.smith@example.com', role: 'Organizer' },
    { id: 3, name: 'Carol Davis', email: 'carol.davis@example.com', role: 'Participant' }
];

const UserManagement = () => {
    const [users, setUsers] = useState(initialUsers);
    const [showForm, setShowForm] = useState(false);
    const [newUser, setNewUser] = useState({ name: '', email: '', role: '' });

    const handleAddUser = () => {
        if (newUser.name && newUser.email && newUser.role) {
            setUsers([...users, { id: users.length + 1, ...newUser }]);
            setNewUser({ name: '', email: '', role: '' });
            setShowForm(false);
        }
    };

    return (

        <div className="user-management">
            <h1>Manage Users</h1>
            <Sidebar />
            <button onClick={() => setShowForm(!showForm)} className="btn-add-user">
                {showForm ? 'Cancel' : 'Add New User'}
            </button>

            {showForm && (
                <div className="user-form">
                    <input
                        type="text"
                        placeholder="Name"
                        value={newUser.name}
                        onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        value={newUser.email}
                        onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Role"
                        value={newUser.role}
                        onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
                    />
                    <button onClick={handleAddUser} className="btn-submit">Add User</button>
                </div>
            )}

            <table className="user-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                            <td>
                                <button className="btn-edit">Edit</button>
                                <button className="btn-delete">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserManagement;
