// src/pages/EventDetailPage.js
import React, { useEffect, useState } from 'react';
import EventDetail from '../components/EventDetail';
import AttendeeList from '../components/AttendeeList';
import AttendeeForm from '../components/AttendeeForm';
import './EventDetailPage.css';

const EventDetailPage = ({ match }) => {
  const [event, setEvent] = useState(null);
  const [attendees, setAttendees] = useState([]);

  useEffect(() => {
    const eventId = match.params.id;
    // Fetch event details and attendees from API
    // Example data
    setEvent({ id: eventId, name: 'Event 1', date: '2024-10-01', location: 'Location 1' });
    setAttendees([
      { id: 1, name: 'Attendee 1' },
      { id: 2, name: 'Attendee 2' },
    ]);
  }, [match.params.id]);

  const handleAttendeeSubmit = (attendee) => {
    // Handle attendee addition, e.g., send data to API
    console.log('Attendee added:', attendee);
  };

  return (
    <div className="event-detail-page">
      {event && <EventDetail event={event} />}
      <AttendeeList attendees={attendees} />
      <AttendeeForm onSubmit={handleAttendeeSubmit} />
    </div>
  );
};

export default EventDetailPage;
