import React, { useState, useRef } from 'react';
import { QRCodeCanvas } from 'qrcode.react';

import './EventDetail.css'

const InviteePage = ({ match }) => {
  const [eventDetails, setEventDetails] = useState({
    id: 1,
    name: 'Sample Event',
    date: '2024-09-10',
    location: 'Sample Location',
    description: 'This is a sample event description for demonstration purposes.',
  });
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const qrCodeRef = useRef(null); // Ref to access the QR code component

  const handleGenerateQrCode = () => {
    if (eventDetails) {
      // URL to encode in QR code
      const url = `${window.location.origin}/events/${eventDetails.id}/attend`;
      setQrCodeUrl(url);
    }
  };

  const handleDownloadQrCode = () => {
    const canvas = qrCodeRef.current.querySelector('canvas');
    if (canvas) {
      const imgURL = canvas.toDataURL('image/png');
      const link = document.createElement('a');
      link.href = imgURL;
      link.download = 'event-qr-code.png';
      link.click();
    }
  };

  return (
    <div className="invitee-page">
      <h2>{eventDetails.name}</h2>
      <p><strong>Date:</strong> {eventDetails.date}</p>
      <p><strong>Location:</strong> {eventDetails.location}</p>
      <p><strong>Description:</strong> {eventDetails.description}</p>

      <button onClick={handleGenerateQrCode} className="btn btn-primary">
        Generate QR Code
      </button>

      {qrCodeUrl && (
        <div className="qr-code-display" ref={qrCodeRef}>
          <QRCodeCanvas value={qrCodeUrl} />
          <button onClick={handleDownloadQrCode} className="btn btn-secondary">
            Download QR Code
          </button>
        </div>
      )}
    </div>
  );
};

export default InviteePage;
