import React, { useState } from 'react';
import './ReportManagement.css';
import Sidebar from './Sidebar';

const initialReports = [
    { id: 1, title: 'Annual Event Summary', date: '2024-01-01', status: 'Completed' },
    { id: 2, title: 'Summer Festival Overview', date: '2024-06-25', status: 'Pending' },
    { id: 3, title: 'Tech Summit Attendance', date: '2024-07-15', status: 'Completed' }
];

const ReportManagement = () => {
    const [reports, setReports] = useState(initialReports);
    const [showForm, setShowForm] = useState(false);
    const [newReport, setNewReport] = useState({ title: '', date: '', status: 'Pending' });

    const handleAddReport = () => {
        if (newReport.title && newReport.date) {
            setReports([...reports, { id: reports.length + 1, ...newReport }]);
            setNewReport({ title: '', date: '', status: 'Pending' });
            setShowForm(false);
        }
    };

    return (
        <div className="report-management">
            <h1>Manage Reports</h1>
            <button onClick={() => setShowForm(!showForm)} className="btn-add-report">
                {showForm ? 'Cancel' : 'Generate New Report'}
            </button>

            {showForm && (
                <div className="report-form">
                    <input
                        type="text"
                        placeholder="Report Title"
                        value={newReport.title}
                        onChange={(e) => setNewReport({ ...newReport, title: e.target.value })}
                    />
                    <input
                        type="date"
                        placeholder="Report Date"
                        value={newReport.date}
                        onChange={(e) => setNewReport({ ...newReport, date: e.target.value })}
                    />
                    <select
                        value={newReport.status}
                        onChange={(e) => setNewReport({ ...newReport, status: e.target.value })}
                    >
                        <option value="Pending">Pending</option>
                        <option value="Completed">Completed</option>
                    </select>
                    <button onClick={handleAddReport} className="btn-submit">Generate Report</button>
                </div>
            )}

            <table className="report-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Date</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {reports.map((report) => (
                        <tr key={report.id}>
                            <td>{report.id}</td>
                            <td>{report.title}</td>
                            <td>{report.date}</td>
                            <td>{report.status}</td>
                            <td>
                                <button className="btn-view">View</button>
                                <button className="btn-download">Download</button>
                                <button className="btn-delete">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ReportManagement;
