import React, { useState } from 'react';
import './EventManagement.css';
import Sidebar from './Sidebar';

const initialEvents = [
    { id: 1, name: 'Conference 2024', date: '2024-05-15', location: 'New York City' },
    { id: 2, name: 'Summer Festival', date: '2024-06-22', location: 'Los Angeles' },
    { id: 3, name: 'Tech Summit', date: '2024-07-10', location: 'San Francisco' }
];

const EventManagement = () => {
    const [events, setEvents] = useState(initialEvents);
    const [showForm, setShowForm] = useState(false);
    const [newEvent, setNewEvent] = useState({ name: '', date: '', location: '' });

    const handleAddEvent = () => {
        if (newEvent.name && newEvent.date && newEvent.location) {
            setEvents([...events, { id: events.length + 1, ...newEvent }]);
            setNewEvent({ name: '', date: '', location: '' });
            setShowForm(false);
        }
    };

    return (
        <div className="event-management">

            <h1>Manage Events</h1>
            <button onClick={() => setShowForm(!showForm)} className="btn-add-event">
                {showForm ? 'Cancel' : 'Add New Event'}
            </button>

            {showForm && (
                <div className="event-form">
                    <input
                        type="text"
                        placeholder="Event Name"
                        value={newEvent.name}
                        onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
                    />
                    <input
                        type="date"
                        placeholder="Event Date"
                        value={newEvent.date}
                        onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Event Location"
                        value={newEvent.location}
                        onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
                    />
                    <button onClick={handleAddEvent} className="btn-submit">Add Event</button>
                </div>
            )}

            <table className="event-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Date</th>
                        <th>Location</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {events.map((event) => (
                        <tr key={event.id}>
                            <td>{event.id}</td>
                            <td>{event.name}</td>
                            <td>{event.date}</td>
                            <td>{event.location}</td>
                            <td>
                                <button className="btn-edit">Edit</button>
                                <button className="btn-delete">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default EventManagement;
