import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Assuming you're using React Router for navigation

import './'

// Mock function to simulate fetching events from an API
const fetchEvents = async () => {
  // Replace with actual API call
  return [
    { id: 1, name: 'Event 1', date: '2024-09-10', location: 'Location 1', privacy: 'public' },
    { id: 2, name: 'Event 2', date: '2024-09-12', location: 'Location 2', privacy: 'private' },
  ];
};

const EventManagementPage = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const loadEvents = async () => {
      const eventData = await fetchEvents();
      setEvents(eventData);
    };

    loadEvents();
  }, []);

  const handleDelete = (eventId) => {
    // Implement delete logic here, e.g., send a DELETE request to your API
    console.log(`Deleting event with ID: ${eventId}`);
    setEvents(events.filter(event => event.id !== eventId));
  };

  return (
    <div className="event-management-page">
      <h2>Manage Events</h2>
      <Link to="/create-event" className="btn btn-primary">Create New Event</Link>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Date</th>
            <th>Location</th>
            <th>Privacy</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {events.map(event => (
            <tr key={event.id}>
              <td>{event.name}</td>
              <td>{event.date}</td>
              <td>{event.location}</td>
              <td>{event.privacy}</td>
              <td>
                <Link to={`/events/${event.id}`} className="btn btn-info">View</Link>
                <Link to={`/edit-event/${event.id}`} className="btn btn-warning">Edit</Link>
                <button onClick={() => handleDelete(event.id)} className="btn btn-danger">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EventManagementPage;
